/* eslint no-console: 0 */

const term = require('../../util/term');


const defaultLogger = {
    debug: (message = '') => {
        if (process.env.DEBUG) {
            console.debug(message);
        }
    },

    log: (message = '') => {
        console.log(message);
    },

    warn: (message = '') => {
        console.warn(term.warn(message));
    },

    error: (message = '') => {
        console.error(term.error(message));
    }
}

defaultLogger.isTTY = process.stdout.isTTY;

const nullLogger = {
    debug: () => { },
    log: () => { },
    warn: () => { },
    error: () => { },
    write: () => { }
}

module.exports = {
    defaultLogger,
    nullLogger
}
